import React, { useState } from "react";
import styled from "styled-components/native";
import { TouchableWithoutFeedback, Keyboard } from "react-native";
import AuthButton from "../../components/AuthButton";
import AuthInput from "../../components/AuthInput";
import useInput from "../../hooks/useInput";
import { Alert } from "react-native";
import { useMutation } from "react-apollo-hooks";
import { CREATE_ACCOUNT } from "./AuthQueries";

const View = styled.View`
  justify-content: center;
  align-items: center;
  flex: 1;
`;

export default ({ navigation }) => {
  const fNameInput = useInput("");
  const lNameInput = useInput("");
  const emailInput = useInput(navigation.getParam("email", ""));
  const usernameInput = useInput("");
  const PwInput = useInput("");
  const [loading, setLoading] = useState(false);
  const [createAccountMutation] = useMutation(CREATE_ACCOUNT, {
    variables: {
      username: usernameInput.value,
      email: emailInput.value,
      firstName: fNameInput.value,
      lastName: lNameInput.value,
      password: PwInput.value
    }
  });

  const handleSingup = async () => {
    const { value: email } = emailInput;
    const { value: fName } = fNameInput;
    const { value: lName } = lNameInput;
    const { value: username } = usernameInput;
    const { value: password } = PwInput;
    const emailRegex = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    const pwdRegex = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/;
    
    if (!emailRegex.test(email)) {
      return Alert.alert("ì´ë©”ì¼ì„ ì œëŒ€ë¡œ ì…ë ¥í•´ ì£¼ì„¸ìš”!");
    }
    if (fName === "" || lName === "") {
      return Alert.alert("ì´ë¦„ì„ ì œëŒ€ë¡œ ì…ë ¥í•´ ì£¼ì„¸ìš”.");
    }
    if (username === "") {
      return Alert.alert("ë‹‰ë„¤ì„ì„ ì œëŒ€ë¡œ ì…ë ¥í•´ ì£¼ì„¸ìš”.");
    }
    if (password === "") {
      return Alert.alert("ë¹„ë°€ë²ˆí˜¸ë¥¼ ì…ë ¥í•´ ì£¼ì„¸ìš”!");
    }
    if (!pwdRegex.test(password)) {
      return Alert.alert("ë¹„ë°€ë²ˆí˜¸ ì–‘ì‹ì„ í™•ì¸í•´ì£¼ì„¸ìš”.");
    }
    try {
      setLoading(true);
      const {
        data: { createAccount }
      } = await createAccountMutation();
      if (createAccount) {
        Alert.alert("íšŒì›ê°€ì… ì„±ê³µ!", "ì§€ê¸ˆ ë¡œê·¸ì¸í•˜ëŸ¬ ê°€ë³¼ê¹Œìš”?");
        navigation.navigate("Login", { email });
      }
    } catch (e) {
      console.log(e);
      Alert.alert("Woops! ë‹‰ë„¤ì„ì´ ì¤‘ë³µë¼ìš” ğŸ¤£");
    } finally {
      setLoading(false);
    }
  };
  return (
    <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
      <View>
        <AuthInput
          {...fNameInput}
          placeholder="ì´ë¦„"
          autoCapitalize="words"
        />
        <AuthInput
          {...lNameInput}
          placeholder="ì„±"
          autoCapitalize="words"
        />
        <AuthInput
          {...emailInput}
          placeholder="ì´ë©”ì¼"
          keyboardType="email-address"
          returnKeyType="send"
          autoCorrect={false}
        />
        <AuthInput
          {...usernameInput}
          placeholder="ë‹‰ë„¤ì„"
          returnKeyType="send"
          autoCorrect={false}
        />
        <AuthInput
          {...PwInput}
          secureTextEntry={true}
          placeholder="ë¹„ë°€ë²ˆí˜¸ ìµœì†Œ 8ì ë¬¸ì ìˆ«ì í•˜ë‚˜ ì´ìƒ"
          returnKeyType="send"
        />
        <AuthButton loading={loading} onPress={handleSingup} text="íšŒì›ê°€ì…" />

      </View>
    </TouchableWithoutFeedback>
  );
};
